import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux'

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../Collection/collection.component';
import { firestore , convertCollectionSnapshotsToMap} from '../../firebase/firebase.utils';
import { updateCollections } from '../../redux/shop/shop.actions';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component{
  constructor() {
    super();

    this.state = {
      loading: true
    }
  }
  unSubscribeFromSnapshot = null;

  componentDidMount() {
    const { updatedCollections } = this.props;
    const collectionRef = firestore.collection('collections');
    collectionRef.onSnapshot(async snapshot => {
      const collectionsMap = convertCollectionSnapshotsToMap(snapshot);
      updatedCollections(collectionsMap);
      this.setState({loading: false})
    });
  }

  render(){
    const { match } = this.props;
    return (
      <div className='shop-page'>
        <Route exact path={`${match.path}`} component={CollectionsOverview} />
        <Route path={`${match.path}/:collectionId`} component={CollectionPage} />
      </div>
    );
  }
  };

  const mapDispatchToProps = (dispatch) => ({
    updatedCollections: collectionsMap => dispatch(updateCollections(collectionsMap))
  })

export default connect(null, mapDispatchToProps)(ShopPage);
